import React from 'react';
import createGlobalStyle from 'styled-components'
import fetch from 'isomorphic-unfetch'
import Navigation from '../components/Navigation';
import Head from 'next/head';

function About (props) {
console.warn("data", props)
return <div>
        <Head><title>Personajes de Rick y Morty</title></Head>
        <Navigation></Navigation>
       <h1>Personajes de Rick y Morty desde Next y React</h1>

{
props.data.results.map(data=>(
    <div key={data.id}>
        <div>
        <h3>{data.name}</h3>
        <img src={data.image} alt='Imagen de Personaje de Rick y Morty'></img>
        <p>{data.species}</p>
        <p>{data.gender}</p>
        <p>{data.origin.name}</p>
        <p>{data.status}</p>
        </div>
    </div>
    ))
}
</div>
}

About.getInitialProps = async function(){
    const res = await fetch('https://rickandmortyapi.com/api/character')
    const data = await res.json()
    return {
        data
    }
}
const GlobalStyles = createGlobalStyle`
html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
}
/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure, 
footer, header, hgroup, menu, nav, section {
	display: block;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
	content: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}
`
export default About;
