import React from 'react';
import fetch from 'isomorphic-unfetch'
import Navigation from '../components/Navigation';
import Head from 'next/head';
import GlobalStyles from './index'

function About (props) {
const charContainer = { display:'flex',
                        flexWrap: 'wrap',
                        gridRow: '50% 50%',
                        gridColumn: '5% 30% 30% 30% 5%',
                        height: '100%',

}    
console.warn("data", props)
return <div>
        <GlobalStyles/>
        <Head><title>Personajes de Rick y Morty</title></Head>

       <h1>Personajes de Rick y Morty desde Next y React</h1>
    <div style={charContainer}>
{
props.data.results.map(data=>(
    <div key={data.id} >
        <div>
        <h3>{data.name}</h3>
        <img src={data.image} alt='Imagen de Personaje de Rick y Morty'></img>
        <p>{data.species}</p>
        <p>{data.gender}</p>
        <p>{data.origin.name}</p>
        <p>{data.status}</p>
        </div>
    </div>
    ))
}
</div>
</div>
}

About.getInitialProps = async function(){
    const res = await fetch('https://rickandmortyapi.com/api/character')
    const data = await res.json()
    return {
        data
    }
}

export default About;
